planemo tool_init --force --id "kraken_classified_out" --name "Print classified sequences out to filename" --requirement Kraken@0.10.3-beta --example_command 'kraken --db $DBNAME --classified-out $FILENAME seqs.fa' --example_input seqs.fa --example_output onlyclassified_seqs.txt --help_text 'Classified sequences can be sent to a file for later processing (exclude all unclassified sequences from the output)'